import { useState } from 'react';
import styled from '@emotion/styled';
import { THEME_COLORS } from '../../themes/colors';
import {
  TextField,
  Box,
  useMediaQuery,
  Button,
  InputAdornment,
  IconButton,
  Typography,
} from '@mui/material';
import { FormDialog } from '../dialogs';
import { useForm } from 'react-hook-form';
import Visibility from '@mui/icons-material/Visibility';
import { LoginProps } from '../../interfaces/LoginInterface';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
import { useNavigate } from 'react-router-dom';

const CssTextField = styled(TextField)({
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderRadius: '1.2rem',
    },
    borderRadius: '1.2rem',
  },
});

const keys_for_register = ['ASDF', 'FC02', 'FCX22', 'FCV22'];

interface Props {
  login: () => void;
}

export const Form = ({ login }: Props) => {
  const [viewPassword, setViewPassword] = useState<boolean>(false);
  const [viewConfirmPassword, setViewConfirmPassword] =
    useState<boolean>(false);
  const [codeEntered, setCodeEntered] = useState<string>('');
  const [pageType, setPageType] = useState('login');
  const [open, setOpen] = useState(false);
  const [errorCode, setErrorCode] = useState(false);
  const isLogin = pageType === 'login';
  const isRegister = pageType === 'register';
  const isNonMobile = useMediaQuery('(min-width:600px)');
  const navigate = useNavigate();

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setErrorCode(false);
    setCodeEntered('');
  };



  const changeForm = () => {
    if (pageType === 'login') {
      if (keys_for_register.includes(codeEntered)) {
        setPageType('register');
        setErrorCode(false);
        setCodeEntered('');
        setViewPassword(false);
        handleClose();
      } else {
        setPageType('login');
        setErrorCode(true);
        setTimeout(() => {
          setErrorCode(false);
        }, 3000);
      }
    }

    if (pageType === 'register') setPageType('login');
  };

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    watch,
  } = useForm<LoginProps>();

  const onFormSubmit = (data: LoginProps) => {
    console.log(data);
    // login();
  };

  const password = watch('password');

  return (
    <>
      <Box p="1rem 6%" mb={1} textAlign="center">
        <Typography fontWeight="700" variant="h4" color="primary">
          {isLogin ? 'Inicio de sesión' : 'Registro'}
        </Typography>
      </Box>
      <form onSubmit={handleSubmit(onFormSubmit)} noValidate>
        <Box
          display="grid"
          gap="20px"
          gridTemplateColumns="repeat(4, minmax(0, 1fr))"
          sx={{
            '& > div': { gridColumn: isNonMobile ? undefined : 'span 4' },
          }}
        >
          {isRegister && (
            <>
              <CssTextField
                label="Nombre"
                sx={{ gridColumn: 'span 2' }}
                required
                {...register('register.name', {
                  required: 'Este campo es obligatorio',
                })}
                error={!!errors.register?.name}
                helperText={errors.register?.name?.message}
              />
              <CssTextField
                label="Apellidos"
                sx={{ gridColumn: 'span 2' }}
                required
                {...register('register.lastName', {
                  required: 'Este campo es obligatorio',
                })}
                error={!!errors.register?.lastName}
                helperText={errors.register?.lastName?.message}
              />
            </>
          )}

          <CssTextField
            label="Correo electrónico"
            type="email"
            sx={{ gridColumn: 'span 4' }}
            required
            {...register('email', {
              required: 'Este campo es obligatorio',
            })}
            error={!!errors.email}
            helperText={errors.email?.message}
          />
          <CssTextField
            label="Contraseña"
            sx={{ gridColumn: 'span 4' }}
            required
            {...register('password', {
              required: 'Este campo es obligatorio',
            })}
            error={!!errors.password}
            helperText={errors.password?.message}
            type={viewPassword ? 'text' : 'password'}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={() => setViewPassword(!viewPassword)}>
                    {viewPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />

          {isRegister && (
            <CssTextField
              label="Repita su contraseña"
              sx={{ gridColumn: 'span 4' }}
              onPaste={(e) => {
                e.preventDefault();
                return false;
              }}
              required
              {...register('register.repeatPassword', {
                required: 'Este campo es obligatorio',
                validate: (value) =>
                  value === password || 'Las contraseñas no coinciden',
              })}
              error={!!errors.register?.repeatPassword}
              helperText={errors.register?.repeatPassword?.message}
              type={viewConfirmPassword ? 'text' : 'password'}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton
                      onClick={() =>
                        setViewConfirmPassword(!viewConfirmPassword)
                      }
                    >
                      {viewConfirmPassword ? <VisibilityOff /> : <Visibility />}
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />
          )}
        </Box>

        <Button
          fullWidth
          type="submit"
          sx={{
            m: '2rem 0',
            p: '1rem',
            borderRadius: '1.2rem',
            '&:hover': {
              color: '#000',
              backgroundColor: THEME_COLORS.BACKGROUND_COLOR,
              border: `1px solid ${THEME_COLORS.PRIMARY}`,
            },
          }}
        >
          {isLogin ? 'INICIAR SESIÓN' : 'REGISTRARSE'}
        </Button>
        <Box textAlign="end">
          <Typography
            onClick={() => {
              // setPageType(isLogin ? 'register' : 'login');
              if (pageType === 'login') {
                handleClickOpen();
              } else {
                setPageType('login');
              }
              reset();
            }}
            sx={{
              '&:hover': {
                cursor: 'pointer',
              },
            }}
            component="span"
          >
            {isLogin ? (
              <p style={{ color: 'black' }}>
                <b>Regístrarse</b>{' '}
              </p>
            ) : (
              <p style={{ color: 'black' }}>
                <b>Iniciar Sesión</b>{' '}
              </p>
            )}
          </Typography>
        </Box>
        <FormDialog
          open={open}
          handleClose={handleClose}
          setCodeEntered={setCodeEntered}
          codeEntered={codeEntered}
          changeForm={changeForm}
          errorCode={errorCode}
        />
      </form>
    </>
  );
};
